mapper.select:
UPDATE filelist
SET fsize = fsize + #{fsize}
WHERE fpath = #{ppath} AND fname != #{fname}

SELECT COUNT(*)
FROM filelist
WHERE fpath = #{fpath} AND fname=#{fname}

UPDATE filelist
SET fsize=#{fsize}, fDate=#{fdate}, udate=sysdate
WHERE fpath = #{fpath} AND fname=#{fname}

UPDATE filelist a
SET a.fsize = a.fsize + #{fsize} - 
	(
		SELECT b.fsize
		FROM filelist b
		WHERE b.fname=#{fname}
	)
WHERE a.fpath = #{ppath}

UPDATE filelist a
SET a.fsize = a.fsize - (
	SELECT b.fsize
	FROM filelist b
	WHERE fname = #{fname}
)
WHERE fpath = #{ppath}

UPDATE filelist
SET fpath = #{nextPath}, ppath=#{ppath} 
WHERE fpath =#{prevPath} AND fname=#{fname}
